from Crypto.Util.number import *
from secret import flag

flag = list(bin(bytes_to_long(flag))[2:])
assert(len(flag) == 351)

for i in range(len(flag)):
    flag[i] = int(flag[i])

for i in range(2000):
    flag.append(flag[i] ^ flag[i+100] ^ flag[i+200] ^ flag[i+300] ^ flag[-1])
    print(flag[-1],end = "")

