from Crypto.Util.number import *
import random
from secret import flag

def pad(data,bit):
    temp = len(bin(data)[2:])
    pad_length = bit - temp
    pad_data = (data << pad_length) + random.getrandbits(pad_length)
    return pad_data

m = pad(bytes_to_long(flag),1024)

def get_prime(bit):
    while(1):
        p = 2 * 7 * getPrime(bit) + 1
        if(isPrime(p)):
            return p

p = get_prime(512)
q = getPrime(512)
n = p*q
e = 7*7*7*7*7

c = pow(m,e,n)
print("p =",p)
print("q =",q)
print("n =",n)
print("c =",c)

